module.exports.emitter = emitter;

const logger = require("./Log").getLogger(__filename);
const data = require('../res/data/itemCategories.json');

const equipmentBaseTypes = data.equipmentBaseTypes;
const gemBaseTypes = data.gemBaseTypes;
const otherBaseTypes = data.otherBaseTypes;
const nonStackableBaseTypes = [].concat(Object.keys(equipmentBaseTypes), Object.keys(gemBaseTypes));

const metamorphSamples = [
  "BrainInventory", "LungInventory", "HeartInventory", "LiverInventory", "EyeballInventory"
];

const nonStackableBulkItems = [
  'Map Fragments',
  'Prophecy',
  'Labyrinth Items',
  'Maps',
  'Incubator',
  'Atlas Region Upgrade Item'    
];

function getCategory(item, subcategory = false) {

  // handle hybrid gems
  var t = (item.hybrid ? item.hybrid.baseTypeName : item.typeLine);
  if(!t) return null;

  if(t === "Expedition Logbook") {
    return t;
  }

  if(t.includes("Contract")) {
    return data.heistQuestItems.includes(t) ? "Quest Items" : "Contract";
  }

  if(t.includes("Blueprint")) {
    return "Blueprint";
  }

  if(otherBaseTypes[t]) {
    if(!subcategory && Array.isArray(otherBaseTypes[t])) {
      return otherBaseTypes[t][0];
    } else {
      return otherBaseTypes[t];
    }
  }

  switch(item.frameType) {
    case 4:
      var n = t.replace(/(Superior|Anomalous|Divergent|Phantasmal) /g, "");
      if(gemBaseTypes[n]) {
        return gemBaseTypes[n];
      } else {
        logger.info(`No base type found for gem [${t}]`);
        return "";
      }
    case 5:  
      if(t.startsWith("Captured Soul")) {
        return "Pantheon Soul";
      } else if(t.endsWith("Seed") || t.endsWith("Grain") || t.endsWith("Bulb") || t.endsWith("fruit")) {
        return "Harvest Seed";
      }
      return "Labyrinth Items";
    case 6:
      return "Divination Card";
    case 7:
      return "Quest Items";
    case 8:
      return "Prophecy";
  }

  if(t.endsWith("Scarab")) {
    return (subcategory ? ["Map Fragments", "Scarab"] : "Map Fragments");
  }

  if(t.includes("Watchstone")) {
    return "Atlas Region Upgrade Item";
  }

  // Maligaro's Map quest item has frameType 7, already detected above as a quest item
  if(t.includes(" Map")) {
    return "Maps";
  }

  if(t.endsWith("Incubator")) {
    return "Incubator";
  }

  if(t.endsWith("Piece")) {
    return "Harbinger Item Piece";
  }

  if(item.icon.includes("BestiaryOrbFull")) {
    return "Captured Beast";
  }

  // 3.9 metamorph inventory organs
  for(var i = 0; i < metamorphSamples.length; i++) {
    if(item.icon.includes(metamorphSamples[i])) return "Metamorph Sample";
  }

  // equipment - search by hardcoded basetype
  t = t.replace("Superior ", "");

  // non-magic equipment
  if(item.frameType !== 1) {
    if(equipmentBaseTypes[t]) {
      return equipmentBaseTypes[t];
    }
  }

  // magic equipment - typeline is polluted by prefixes $%&*#^@!!!
  var keys = Object.keys(equipmentBaseTypes);
  for(var i = 0; i < keys.length; i++) {
    var key = keys[i];
    if(t.includes(key)) {
      return equipmentBaseTypes[key];
    }
  }

  logger.info(`No category found for item ${item.id || "(no id)"}! JSON follows:`);
  logger.info(JSON.stringify(item));
  return null;  

}

function getEquipmentBaseType(str) {
  var types = Object.keys(equipmentBaseTypes);
  for(var i = 0; i < types.length; i++) {
    if(str.includes(types[i])) {
      return types[i];
    }
  }
  return null;
}

function isNonStackable(str) {
  return nonStackableBaseTypes.includes(str);
}

module.exports.getCategory = getCategory;
module.exports.getEquipmentBaseType = getEquipmentBaseType;
module.exports.isNonStackable = isNonStackable;
module.exports.nonStackableBulkItems = nonStackableBulkItems;

const ItemCategoryParser = require("./ItemCategoryParser");

var Rarity = {
	Normal: 0,
	Magic: 1,
	Rare: 2,
	Unique: 3,

	getName: function (i) {
		switch (i) {
			case 0: return "Normal";
			case 1: return "Magic";
			case 2: return "Rare";
			case 3: return "Unique";
			default: throw 'Invalid Rarity: ' + i
		}
	},

	isValid: function (i) {
	    return 0 <= i && i <= 3
	},

	parse: function (str) {
		switch (str.toLowerCase()) {
			case 'normal': return 0;
			case 'magic': return 1;
			case 'rare': return 2;
			case 'unique': return 3;
			default: throw "Invalid Rarity: " + str;
		}
	}
};

var Colors = {
  BLACK_75 : {r:0, g:0, b:0, a:190},
  WHITE : {r:200, g:200, b:200},
  BLUE : {r:136, g:136, b:255},
  YELLOW : {r:255, g:255, b:119},
  ORANGE : {r:175, g:96, b:37},
  GOLD : {r:170, g:158, b:130},
  CYAN : {r:27, g:162, b:155},
  GREEN : {r:74, g:230, b:58},
  LIGHT_CYAN : {r:170, g:230, b:230},
  getDefaultColor : function(item) {
    switch(item.rarity) {
      case Rarity.Magic:
        return this.BLUE;
      case Rarity.Rare:
        return this.YELLOW;
      case Rarity.Unique:
        return this.ORANGE;
    }
    switch(item.itemClass) {
      case "Currency":
        return this.GOLD;
      case "Quest Items":
        return this.GREEN;
      case "Divination Card":
        return this.LIGHT_CYAN;
      case "Labyrinth Item":
      case "Labyrinth Trinket":
        return this.GOLD;
    }
    if(item.itemClass.includes("Gem")) {
      return this.CYAN;
    }
    return this.WHITE;
  }  
};

class ItemData {

static countSockets(sockets) {

  if(!sockets || !sockets.length) return 0;

	var result = 0;  
	sockets.forEach( function(group) {
    group = group.replace("DV", "D");
    result += group.length;
	});
	return result;
}

static getQuality(data) {
  if(!data.properties) return null;
  for(var i = 0; i < data.properties.length; i++) {
    if(data.properties[i].name === "Quality") {
      return (data.properties[i].values[0][0]).slice(1, -1);
    }
  }
  return null;
}

static getLevelRequirement(data) {

  if(data.properties) {
    for(var i = 0; i < data.properties.length; i++) {
      if(data.properties[i].name === "Map Tier") {
        return Number(data.properties[i].values[0][0]) + 67;
      }
    }
  }

  if(!data.requirements) return null;
  for(var i = 0; i < data.requirements.length; i++) {
    if(data.requirements[i].name === "Level") {
      return data.requirements[i].values[0][0];
    }
  }
  return null;

}

static getSockets(data) {

  if(!data.sockets) return [];

  var sockets = [];
  var currGroup = 0;
  var currSockets = "";

  for(var i = 0; i < data.sockets.length; i++) {
    var s = data.sockets[i];

    if(s.group === currGroup) {
      currSockets += s.sColour;
    } else {
      sockets.push(currSockets);
      currGroup = s.group;
      currSockets = s.sColour;
    }
  }
  sockets.push(currSockets);

  return sockets;

}

static getRarity(data) {
  return (data.frameType < 1 || data.frameType > 3) ? 0 : data.frameType;
}

static getGemLevel(data) {

  if(!data.properties) return false;

  if(data.frameType !== 4) return false;

  for(var i = 0; i < data.properties.length; i++) {
    if(data.properties[i].name === "Level") {
      return (data.properties[i].values[0][0].replace(" (Max)", ""));
    }
  }
  return false;
}

static getInfluence(data) {
  if(data.influences) {
    return Object.keys(data.influences).map(inf => { return inf.toLowerCase() } );
  }
  if(data.shaper) {
    return ["shaper"];
  }
  if(data.elder) {
    return ["elder"];
  }
  return [];
}

static getMapTier(data) {
  if(!data.properties) return null;
  for(var i = 0; i < data.properties.length; i++) {
    if(data.properties[i].name === "Map Tier") {
      return (data.properties[i].values[0][0]);
    }
  }
  return null;
}


static isShapedMap(data) {
  if(!data.properties) return false;
  for(var i = 0; i < data.properties.length; i++) {
    if(data.properties[i].name === "Map Tier") {
      return (data.properties[i].values[0][1] === 1 && data.properties[i].values[0][0] < 16);
    }
  }
  return false;
}

static isElderMap(data) {
  if(!data.properties) return false;
  for(var i = 0; i < data.properties.length; i++) {
    if(data.properties[i].name === "Map Tier") {
      return (data.properties[i].values[0][1] === 1 && data.properties[i].values[0][0] === 16);
    }
  }
  return false;
}

static isBlightedMap(data) {
  if(!data.properties) return false;
  return data.icon.includes("mb=1");
}

static createItem(itemdata)
{
  var obj = {};

	obj.rawData = itemdata;
  obj.styleModifiers = itemdata.styleModifiers || {};

	obj.name = itemdata.name.replace("<<set:MS>><<set:M>><<set:S>>", "").replace(/<>/g, "");
  obj.id = itemdata.id;

	obj.itemLevel = Math.max(1, itemdata.ilvl);
	obj.dropLevel = Math.max(1, ItemData.getLevelRequirement(itemdata));

	obj.quality = ItemData.getQuality(itemdata);
	obj.rarity = ItemData.getRarity(itemdata);

	obj.itemClass = ItemCategoryParser.getCategory(itemdata);

  // handle hybrid gems
  obj.baseType = itemdata.typeLine.replace("<<set:MS>><<set:M>><<set:S>>", "").replace(/<>/g, "");
  if(itemdata.hybrid && !obj.baseType.startsWith("Vaal")) {
    obj.baseType = itemdata.hybrid.baseTypeName;
  }

	obj.identified = itemdata.identified || false;
	obj.corrupted = itemdata.corrupted || false;
  obj.mirrored = itemdata.duplicated || false;
	obj.influence = ItemData.getInfluence(itemdata);
	obj.shapedMap = ItemData.isShapedMap(itemdata);
	obj.elderMap = ItemData.isElderMap(itemdata);
  obj.blightedMap = ItemData.isBlightedMap(itemdata);
  obj.mapTier = ItemData.getMapTier(itemdata);
	obj.stackSize = itemdata.stackSize;
  obj.replica = itemdata.replica;

  obj.veiled = itemdata.veiled;
  obj.synthesised = itemdata.synthesised || false;
  obj.fractured  = itemdata.fractured || false;

  obj.explicitMods = itemdata.explicitMods;
  obj.implicitMods = itemdata.implicitMods;
  obj.enchantMods = itemdata.enchantMods;

  if(obj.itemClass === "Prophecy") {
    obj.name = obj.baseType;
    obj.baseType = obj.itemClass;
    obj.itemClass = "Currency";
  }

	obj.width = itemdata.w;
	obj.height = itemdata.h;

	obj.sockets = ItemData.getSockets(itemdata);
	obj.gemLevel = ItemData.getGemLevel(itemdata);

	obj.outerElement = null;
	obj.domElement = null;

	obj.getDisplayName = function() {
		if (!this.identified && this.quality > 0) {
			return this.baseType;
		}
		if (!this.identified || !this.name) {
      var name = (this.stackSize > 1 ? this.stackSize + " x " : "") + this.baseType;
      if(this.gemLevel) {
        if(this.quality > 0 && !name.includes("Anomalous") && !name.includes("Divergent") && !name.includes("Phantasmal")) {
          name = "Superior " + name;
        }
        if(this.gemLevel > 1) name += ` (Level ${this.gemLevel})`;
      }
      return name;
		}
		else {
			return this.name + (this.baseType === "Prophecy" ? "" : "<BR>" + this.baseType);
		}
	}

	obj.getNumSockets = function() {
		return ItemData.countSockets( this.sockets );
	}

  // broken - mod list currently contains only the actual stats
  // can't get actual name of mod, which is what's being searched for  
	obj.hasExplicitMod = function(mod) {
    if(mod === "Veil" && this.veiled) {
      return true;
    } else {
      if(!this.explicitMods) {
        return false;
      } else {
        for(var i = 0; i < this.explicitMods.length; i++) {
          if(this.explicitMods[i].includes(mod)) return true;
        }
        return false;
      }
    }
	}  

	obj.hasEnchantment = function(mod) {
    if(!this.enchantMods) {
      return false;
    }
    var str = mod.replace("Enchantment ", "");
    return this.enchantMods.some(function(mod) {
      return mod.includes(str);
    });
	}  

	obj.draw = function() {

		var outerDiv = document.createElement( 'div' );
		outerDiv.className = 'item-container';
    outerDiv.id = this.id;

		var itemDiv = document.createElement( 'div' );
		itemDiv.className = 'item';

    var iconDiv = this.getItemIcon();
    if(iconDiv) {
      itemDiv.append(iconDiv);
    }

		var itemName = document.createElement( 'span' );
    itemName.classList.add('item-name');
		itemName.innerHTML = this.getDisplayName();

		itemDiv.appendChild( itemName );

		if (this.getNumSockets() > 0 && !this.sockets.includes("DV")) {
			itemDiv.appendChild( drawSockets(this) );
		}

		outerDiv.appendChild( itemDiv );

		this.outerElement = outerDiv;
		this.domElement = itemDiv;    
    this.applyStyles();

		return outerDiv;

	}

  obj.applyStyles = function(s = this.styleModifiers) {
    this.setFontSize( s.fontSize || 32 );
    this.setBackgroundColor( s.backgroundColor || Colors.BLACK_75 );
    this.setTextColor( s.textColor || Colors.getDefaultColor(this) );
    this.setBorderColor( s.borderColor || Colors.getDefaultColor(this) );
  }

  obj.getStyleFromFilter = function(filter) {

    if(!filter) return;

    var lastMatchedRule = null;
    for (var j = 0; j < filter.ruleSet.length; j++) {
      var rule = filter.ruleSet[j];
      if(rule.match(this)) {
        lastMatchedRule = rule;
        if(!rule.continue) {
          break;
        }
      }
    }

    if(lastMatchedRule) {
      lastMatchedRule.applyTo(this);
    }

  }

  obj.getItemIcon = function() {
    var influenceIcons = [];
    if(this.veiled) {
      influenceIcons.push("veiled");
    } else if(this.synthesised) {
      influenceIcons.push("synthesised");
    } else if(this.fractured) {
      influenceIcons.push("fractured");
    } else {
      if(this.influence) {
        this.influence.forEach(inf => influenceIcons.push(inf));
      }
    }
    if(influenceIcons.length) {
      var iconDiv = document.createElement('span');
      influenceIcons.forEach(icon => {
        iconDiv.innerHTML += `<img src="res/img/${icon}.png" style="width:24px;height:24px;margin:4px 4px 0px 0px"/>`;
      });
      return iconDiv;
    } else {
      return null;
    }    
  }

	obj.setTextColor = function (color) {
		getLabel( this ).style.color = buildCssColor( color );
	}

	obj.setBorderColor = function (color) {
		this.domElement.style.border = '1px solid ' + buildCssColor( color );
	}

	obj.setBackgroundColor = function (color) {
		this.domElement.style.backgroundColor = buildCssColor( color );
	}

	obj.setFontSize = function (size) {
		var actualSize = MathUtils.remap( size, 18, 45, 8, 24 );
		getLabel( this ).style.fontSize = (Math.round(actualSize)).toString() + 'px';
	}

  return obj;

	function buildCssColor (color) {
		var r = color.r;
		var g = color.g;
		var b = color.b;
		var a = 1;
		if (color.hasOwnProperty( 'a' )) {
			a = color.a / 255; // CSS wants its alpha value between 0 and 1
		}
		return 'rgba(' + r.toString() + ',' + g.toString() + ',' + b.toString() + ',' + a.toString() + ')';
	}

	function getLabel (self) {
		for (var i=0; i < self.domElement.children.length; i++) {
			var child = self.domElement.children[i];
			if ((child.tagName.toLowerCase() === 'span') && child.classList.contains('item-name')) {
				return child;
			}
		}
		return null;
	}

	function computeSocketPadding (numSockets) {

		// The height values as computed by the formula below:
		//	1: 4,
		//	2: 4,
		//	3: 10,
		//	4: 10,
		//	5: 16,
		//	6: 16

		var width = ( numSockets == 1 ) ? 4 : 10;
		var height = ( Math.ceil( numSockets / 2 ) - 1 ) * 6 + 4;

		var result = {};
		result.x = 2 + ( 10 - width ) / 2;
		result.y = 2 + ( 16 - height ) / 2;
		return result;
	}

	function computeSocketPaddingSingleColumn (numSockets) {
		// 1: 4
		// 2: 10
		// 3: 16

		var width = 4;
		var height = (numSockets - 1) * 6 + 4;

		var result = {};
		result.x = 2 + ( 10 - width ) / 2;
		result.y = 2 + ( 16 - height ) / 2;
		return result;
	}

	function drawSocket (socketColor) {
		var socket = document.createElement( 'div' );
		socket.className = 'socket';

		switch (socketColor) {
			case 'R':
				socket.style.backgroundColor = '#ff0000';
				break;
			case 'G':
				socket.style.backgroundColor = '#80ff33';
				break;
			case 'B':
				socket.style.backgroundColor = '#8888ff';
				break;
			case 'W':
				socket.style.backgroundColor = '#ffffff';
				break;
			case 'A':
				socket.style.backgroundColor = '#3b3b3b';
				break;
		}

		return socket;
	}

	function drawLink (x, y, padding) {
		var link = document.createElement( 'div' );

		// Doesn't have to be efficient, this is only run once during startup.
		var xy = x.toString() + '/' + y.toString();
		switch (xy) {
			// case '0/0' is not possible because link is created at the second socket!
			case '1/0':
			case '0/1':
			case '1/2':
				link.className = 'link-horizontal';
				link.style.left = (3 + padding.x).toString() + 'px';
				link.style.top = ((y * 6) + 1 + padding.y).toString() + 'px';
				break;
			case '1/1':
			case '0/2':
				link.className = 'link-vertical';
				link.style.left = ((x * 6) + 1 + padding.x).toString() + 'px';
				link.style.top = (((y-1) * 6) + 3 + padding.y).toString() + 'px';
				break;
		}

		return link;
	}

	function drawLinkSingleColumn (y, padding) {
		var link = document.createElement( 'div' );
		link.className = 'link-vertical';
		link.style.left = (1 + padding.x).toString() + 'px';
		link.style.top = (((y-1) * 6) + 3 + padding.y).toString() + 'px';
		return link;
	}

	function incrementSocketPos (x, y) {
		// x0 y0 -> x+1
		// x1 y0 -> y+1
		// x1 y1 -> x-1
		// x0 y1 -> y+1
		// x0 y2 -> x+1

		var xdir = (y % 2 == 1) ? -1 : 1;
		var xstop = (y % 2 == 1) ? 0 : 1;

		if (x != xstop) {
			x += xdir;
		}
		else {
			y += 1;
		}

		return { x:x, y:y };
	}

	function drawSockets (item) {
		if (item.width === 1) {
			return drawSocketsSingleColumn( item );
		} else {
			return drawSocketsTwoColumns( item );
		}
	}

	function drawSocketsTwoColumns (item) {
		var socketsDiv = document.createElement( 'div' );
		socketsDiv.className = 'sockets';

		var padding = computeSocketPadding( item.getNumSockets() );

		var x = 0;
		var y = 0;
		var linked = false;

		item.sockets.forEach( function(group) {
			linked = false;
			var chars = group.split( '' );
			chars.forEach( function(socketColor) {
				var socket = drawSocket( socketColor );
				socket.style.left = (padding.x + (x * 6)).toString() + 'px';
				socket.style.top = (padding.y + (y * 6)).toString() + 'px';
				socketsDiv.appendChild( socket );

				if (linked) {
					var link = drawLink( x, y, padding );
					socketsDiv.appendChild( link );
				}

				var newXY = incrementSocketPos( x, y );
				x = newXY.x;
				y = newXY.y;

				linked = true;
			});
		});

		return socketsDiv;
	}

	function drawSocketsSingleColumn (item) {
		var socketsDiv = document.createElement( 'div' );
		socketsDiv.className = 'sockets';

		var padding = computeSocketPaddingSingleColumn( item.getNumSockets() );

		var y = 0;
		var linked = false;

		item.sockets.forEach( function (group) {
			linked = false;
			var chars = group.split('');
			chars.forEach( function(socketColor) {
				var socket = drawSocket( socketColor );
				socket.style.left = (padding.x).toString() + 'px';
				socket.style.top = (padding.y + (y * 6)).toString() + 'px';
				socketsDiv.appendChild( socket );

				if (linked) {
					var link = drawLinkSingleColumn( y, padding );
					socketsDiv.appendChild( link );
				}

				y += 1
				linked = true;
			});
		});

		return socketsDiv;
	}
};

}

module.exports = ItemData;
module.exports.Rarity = Rarity;

const logger = require("./Log").getLogger(__filename);
const Utils = require("./Utils");
const ItemCategoryParser = require("./ItemCategoryParser");
const rarities = ['Normal', 'Magic', 'Rare', 'Unique', 'Gem', 'Currency', 'Divination Card', 'Quest Item', 'Prophecy', 'Relic'];

async function insertItems(items, timestamp) {
  return new Promise( async (resolve, reject) => {

    var DB = require('./DB').getDB();

    var duplicateInventory = await isDuplicateInventory(items);
    if(duplicateInventory) {
      logger.info(`Duplicate items found for ${timestamp}, returning`);
      resolve();
    } else {
      logger.info(`Inserting items for ${timestamp}`);
      DB.serialize(function() {
        DB.run("begin transaction", (err) => {
          if(err) {
            logger.info(`Error beginning transaction to insert items: ${err}`);
          }
        });
        var stmt = DB.prepare(
          `
            insert into items (id, event_id, icon, name, rarity, category, identified, typeline, sockets, stacksize, rawdata)
            values(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
          `
        );
        Object.keys(items).forEach((key) => {
          var itemToInsert = parseItem(items[key], timestamp);
          stmt.run(itemToInsert, (err) => {
            if(err) {
              logger.info(`Error inserting item: ${err}`);
              logger.info(JSON.stringify(itemToInsert));
            }
          });
        });
        stmt.finalize( (err) => {
          if(err) {
            logger.warn(`Error inserting items for ${timestamp}: ${err}`);
            DB.run("rollback", (err) => {
              if (err) {
                logger.info(`Error rolling back failed item insert: ${err}`);
              }        
            });
          } else {
            DB.run("commit", (err) => {
              if (err) {
                logger.info(`Error committing item insert: ${err}`);
              }        
            });
          }
        });
        logger.info(`Done inserting items for ${timestamp}`);
        resolve();
      });
    }
  });
}

async function isDuplicateInventory(items) {

  return new Promise( (resolve, reject) => {

    var checkDuplicates = false;
    var numItemsToCheck = 0;

    if(items.length === 0) resolve(false);

    var keys = Object.keys(items);
    var query = "select count(1) as count from items where (";
    for(var i = 0; i < keys.length; i++) {

      if(items[keys[i]].stacksize || items[keys[i]].stackSize) continue;

      if(checkDuplicates) {
        query += " or ";
      } else {
        checkDuplicates = true;
      }

      query += `( id = '${keys[i]}' `;
      query += `)`;

      numItemsToCheck++;

    }
    query += ")";

    if(!checkDuplicates || numItemsToCheck < 1) {
      resolve(false);
    } else {
      logger.info(query);
      var DB = require('./DB').getDB();
      DB.get(query, (err, row) => {
        if(err) {
          logger.warn(`Error checking inventory keys: ${err}`);
          resolve(false);
        } else {
          logger.info(`${numItemsToCheck} items in inventory, ${row.count} duplicates found in DB`);
          resolve(row.count === numItemsToCheck);
        }
      });
    }    
  });


}

function parseItem(item, timestamp) {

  var id = item.id;
  var icon = getImageUrl(item.icon);
  var name = stripTags(item.name);
  var rarity = rarities[item.frameType];
  var category = ItemCategoryParser.getCategory(item);
  var identified = item.identified;

  var typeline = stripTags(item.typeLine);
  if(rarity === 'Gem' && item.typeLine !== item.baseType) {
    // to handle hybrid gems (general's cry, predator support)
    typeline = stripTags(item.baseType);
  }


  var stacksize = item.stackSize || null;
  var sockets = Utils.getSockets(item);
  var rawdata = JSON.stringify(item);

  return [id, timestamp, icon, name, rarity, category, identified, typeline, sockets, stacksize, rawdata];
}

function getImageUrl(url) {
  // flask image urls are in a very strange form, just return as is
  if (url.includes("web.poecdn.com/gen")) {
    return url;
  } else {
    // stripping identifier from end
    return url.substring(0, url.indexOf("?"));
  }
}

function stripTags(name) {
  if(!name) {
    return null;
  }
  return name.replace("<<set:MS>><<set:M>><<set:S>>", "");
}

module.exports.insertItems = insertItems;
module.exports.parseItem = parseItem;

const logger = require('./Log').getLogger(__filename);
const Constants = require('./Constants');
const ItemData = require('./ItemData');
const ItemCategoryParser = require("./ItemCategoryParser");
const ItemParser = require('./ItemParser');
const ItemFilter = require('./ItemFilter');
const Utils = require('./Utils');
const zlib = require('zlib');  
const settings = require('./settings').get();  

const baseTypeRarities = ["Normal", "Magic", "Rare"];
const nonPricedCategories = [
  // captured beasts are not acquired as map loot
  // might be relevant for calculating net worth though?
  "Captured Beast",
    // value not tracked for heist items
  "Contract",
  "Blueprint",
  "Trinket",
  "Heist Brooch",
  "Heist Cloak",
  "Heist Gear",
  "Heist Tool"      
];

const log = false;

var ratesCache = {};

function getRatesFor(timestamp, league) {

  if(!league) {
    league = settings.activeProfile.league;
  }    

  if (league.includes("SSF") && !settings.activeProfile.overrideSSF) {
    return null;
  }

  if(!ratesCache[league]) {
    ratesCache[league] = {};
  }

  var date = timestamp.substring(0, 8);
  if(ratesCache[league][date]) {
    return ratesCache[league][date];
  }

  var DB = require('./DB').getLeagueDB(league);
  return new Promise((resolve, reject) => {
    DB.get("select date, data from fullrates where date <= ? or date = (select min(date) from fullrates) order by date desc", [date], async (err, row) => {
      if(err) {
        logger.info(`Unable to get rates for ${date}: ${err}`);
        resolve(null);
      } else {
        if(!row || !row.data) {
          logger.info(`No rates found for ${date}`);
          resolve(null);
        } else {
          zlib.inflate(row.data, (err, buffer) => {
            if(err) {
              // old data - compression not implemented yet, just parse directly
              ratesCache[league][row.date] = JSON.parse(row.data);
            } else {
              ratesCache[league][row.date] = JSON.parse(buffer);
            }
            resolve(ratesCache[league][row.date]);
          });
        }
      }
    });
  });
}

async function price(item, league) {

  // Absolutely unreasonable amounts of pricing trouble. Enough of this!
  if(item.typeline === "Rogue's Marker") {
    return 0;
  }

  if(item.rarity === "Quest Item") {
    // can't be traded
    return 0;
  }
  if(nonPricedCategories.includes(item.category)) {
    return 0;
  }

  let rates = await getRatesFor(item.event_id, league);
  if(!rates) {
    return 0;
  }

  item.parsedItem = JSON.parse(item.rawdata);

  var minItemValue = 0;
  var filter = ItemFilter.filter(item.parsedItem);
  if(filter && filter.ignore) {
    if(filter.minValue) {
      if(filter.option && filter.option === "fullStack" && item.parsedItem.maxStackSize) {
        // logger.info(`Minvalue is ${filter.minValue}, stacksize of ${item.parsedItem.typeLine} is ${item.parsedItem.maxStackSize}, minvalue per card is ${filter.minValue/item.parsedItem.maxStackSize}`);
        minItemValue = filter.minValue / item.parsedItem.maxStackSize;
      } else {
        minItemValue = filter.minValue;
      }
    } else {
      // unconditional ignore - stop here and get vendor recipe instead, if any
      return getVendorRecipeValue();
    }
  }

  if(item.typeline.includes("Watchstone")) {
    return getWatchstoneValue();      
  }    

  var helmetBaseValue;

  if(item.rarity === "Unique") {
    if(item.category === "Maps") {
      return getUniqueMapValue();
    } else {
      // handle helmet enchants - if item is a helmet, don't return value yet
      if(item.category === "Helmets") {
        helmetBaseValue = getUniqueItemValue();
      } else {
        return getUniqueItemValue();
      }
    }
  } else {
    // value not tracked for non-unique flasks and jewels
    if( (item.typeline.includes("Flask") || item.typeline.includes("Jewel")) && baseTypeRarities.includes(item.rarity) ) {
      return 0;
    }
  }

  if(item.typeline.includes("Maven's Invitation")) {
    return getValueFromTable("Invitation");
  }    
  if(
    item.category === "Map Fragments" 
    || (item.category === "Labyrinth Items" && item.typeline.endsWith("to the Goddess")) 
    || item.typeline === "Simulacrum Splinter" 
    || item.typeline === "Crescent Splinter" 
    || (item.typeline.includes("Timeless") && item.typeline.includes("Splinter"))
    || (item.typeline.startsWith("Splinter of"))
  ) {
    return getValueFromTable("Fragment");
  }
  if(item.category === "Harvest Seed") {
    return getSeedValue();
  }
  if(item.rarity === "Currency" || item.typeline.includes("Incubator")) {
    return getCurrencyValue();
  }
  if(item.category === "Maps") {
    return getMapValue();
  }
  if(item.rarity === "Divination Card") {
    return getValueFromTable("DivinationCard");
  }
  if(item.rarity === "Prophecy") {
    return getValueFromTable("Prophecy");
  }
  if(item.category && item.category.includes("Skill Gems")) {
    return getGemValue();
  }

  if(baseTypeRarities.includes(item.rarity) && Constants.baseTypeCategories.includes(item.category)) {
    // handle helmet enchants - if item is a helmet, don't return value yet
    if(item.category === "Helmets") {
      helmetBaseValue = getBaseTypeValue();
    } else {
      return getBaseTypeValue();
    }
  }

  if(helmetBaseValue >= 0) {
    var helmetEnchantValue = getHelmetEnchantValue();
    return Math.max(helmetBaseValue, helmetEnchantValue);
  }

  logger.info(`Unable to get value for item ${item.id || "(no id)"}:`);
  logger.info(JSON.stringify(item.parsedItem));
  return 0;

  /* sub-functions for getting value per item type*/    

  function getValueFromTable(table, identifier = null) {

    // RIP harvest :-(
    if(table === "Seed") return 0;

    if(!rates[table]) {
      logger.info(`No price list found for category ${table}, returning 0`);
      return 0;
    }

    if(!identifier) {
      identifier = item.typeline;
    }

    // special handling for currency shards - always price at 1/20 of the whole orb
    if(Constants.shardTypes[identifier]) {
      let wholeOrb = Constants.shardTypes[identifier];
      let shardValue = rates[table][wholeOrb] / 20;
      let stackValue = shardValue * item.stacksize;
      if(log) {
        if(shardValue >= minItemValue) {
          logger.info(`[${table}] : ${identifier} => ${shardValue} x ${item.stacksize} = ${stackValue}`);
        } else {
          logger.info(`[${table}] : ${identifier} => ${shardValue} < ${minItemValue}, ignoring`);
        }
      }
      return(shardValue >= minItemValue ? stackValue : 0);
    }

    if(settings.alternateSplinterPricing && Constants.fragmentTypes[identifier]) {
      let f = Constants.fragmentTypes[identifier];
      let splinterValue = rates[f.itemType || "Fragment"][f.item] / f.stackSize;
      let stackValue = splinterValue * item.stacksize;
      if(splinterValue >= minItemValue) {
        logger.info(`Using alternate splinter pricing : ${identifier} => ${splinterValue} x ${item.stacksize} = ${stackValue}`);
      } else {
        logger.info(`Using alternate splinter pricing : ${identifier} => ${splinterValue} < ${minItemValue}, ignoring`);
      }
      return(splinterValue >= minItemValue ? stackValue : 0);
    }      

    // handle items that stack - minItemValue is for exactly 1 of the item
    let unitValue = rates[table][identifier];

    if(!unitValue) {
      if(log) {
        logger.info(`[${table}] : ${identifier} => No value found, returning 0`);
      }
      return 0;
    } else if(unitValue < minItemValue) {
      if(log) {
        logger.info(`[${table}] : ${identifier} => ${unitValue} < ${minItemValue}, ignoring`);
      }
      return 0;
    }

    let value = unitValue * (item.stacksize || 1);
    if(log) {
      if(value >= minItemValue) {
        logger.info(`[${table}] : ${identifier} => ${value}`);            
      } else {
        logger.info(`[${table}] : ${identifier} => ${value} < ${minItemValue}, ignoring`);
      }
    }
    return(value >= minItemValue ? value : 0);

  }

  function getHelmetEnchantValue() {
    if(!item.parsedItem.enchantMods) return 0;
    var identifier = item.parsedItem.enchantMods;
    return getValueFromTable("HelmetEnchant", identifier);      
  }    

  function getWatchstoneValue() {
    var identifier = ( item.rarity === "Magic" ? Utils.getWatchstoneBaseType(item.typeline) : (item.name || Utils.getItemName(item.icon)) );
    if(!item.identified) {
      if(Constants.watchstoneMaxCharges[identifier]) {
        identifier += `, ${Constants.watchstoneMaxCharges[identifier]} uses remaining`;
      };
    } else {
      for(var i = 0; i < item.parsedItem.explicitMods.length; i++) {
        var mod = item.parsedItem.explicitMods[i];
        if(mod.endsWith("uses remaining")) {
          identifier += `, ${mod}`;
          break;
        }
      }
    }
    return getValueFromTable("Watchstone", identifier);
  }

  function getGemValue() {

    let typeline = item.typeline.replace("Superior ", "");
    let level = ItemData.getGemLevel(item.parsedItem);
    let quality = ItemData.getQuality(item.parsedItem);
    let corrupted = item.parsedItem.corrupted;      
    let identifier = getFullGemIdentifier(typeline, level, quality, corrupted);

    let value = getValueFromTable("SkillGem", identifier);      
    if(!value && item.parsedItem.hybrid && item.parsedItem.hybrid.baseTypeName) {
      let altIdentifier = getFullGemIdentifier(item.parsedItem.hybrid.baseTypeName, level, quality, corrupted);
      value = value = getValueFromTable("SkillGem", altIdentifier);
    }

    let vendorValue = getVendorRecipeValue();
    return(vendorValue ? Math.max(value, vendorValue.val) : value);

  }

  function getFullGemIdentifier(str, level, quality, corrupted) {
    switch(str) {
      case "Empower Support":
      case "Enlighten Support":
      case "Enhance Support":
        if(level >= 2) str += ` L${level}`;
        break;
      case "Brand Recall":
        if(level >= 6) str += ` L${level}`;
        if(quality >= 20) str += ` Q${quality}`;
        break;
      default:
        if(level >= 20) str += ` L${level}`;
        if(quality >= 20) {
          str += ` Q${quality}`;
        }
        break;
    }
    if(corrupted) {
      str += " (Corrupted)";
    }
    return str;
  }

  function getMapValue() {

    var name = item.typeline.replace("Superior ", "");
    var tier = ItemData.getMapTier(item.parsedItem);
    var series = getSeries(item.parsedItem.icon);

    if(item.rarity === "Magic" && item.identified) {
      // strip affixes from magic item name
      name = Utils.getBaseFromMagicMap(name);
      // special handling for name collision >:\
      if(name === "Vaal Temple Map" && tier < 16) {
        name = "Temple Map";
      }
    }
    var identifier = `${name} T${tier} ${series}`;
    // workaround poe.ninja bug
    var tempIdentifier = identifier.replace("Delirium", "Delerium");
    return getValueFromTable("Map", identifier) || getValueFromTable("Map", tempIdentifier);

    function getSeries(icon) {

      if(icon.includes("https://web.poecdn.com/gen/image/")) {
        return getSeriesBase64(icon);          
      }

      if(icon.includes("mn=")) {
        if(icon.includes("mn=1")) return "Atlas2-3.4";
        if(icon.includes("mn=2")) return "Atlas2";
        if(icon.includes("mn=3")) return "Synthesis";
        if(icon.includes("mn=4")) return "Legion";
        if(icon.includes("mn=5")) return "Blight";
        if(icon.includes("mn=6")) return "Metamorph";
        if(icon.includes("mn=7")) return "Delirium";
        if(icon.includes("mn=8")) return "Harvest";
        if(icon.includes("mn=9")) return "Heist";
        if(icon.includes("mn=10")) return "Ritual";
        if(icon.includes("mn=11")) return "Expedition";
      } else {
        if(icon.includes("2DItems/Maps/AtlasMaps")) return "Atlas";
        if(icon.includes("2DItems/Maps/Map")) return "Pre 2.4";
        if(icon.includes("2DItems/Maps/act4maps")) return "Pre 2.0";
      }
      logger.info(`Invalid map item icon: ${icon}`);
      return "";
    }

  }

  function getSeriesBase64(icon) {

    var data = Utils.getBase64EncodedData(icon);

    if(data.mn) {
      switch(data.mn) {
        case 1: return "Atlas2-3.4";
        case 2: return "Atlas2";
        case 3: return "Synthesis";
        case 4: return "Legion";
        case 5: return "Blight";
        case 6: return "Metamorph";
        case 7: return "Delirium";
        case 8: return "Harvest";
        case 9: return "Heist";
        case 10: return "Ritual";
        case 11: return "Expedition";
      }
    } else {
      if(data.f.includes("2DItems/Maps/AtlasMaps")) return "Atlas";
      if(data.f.includes("2DItems/Maps/Map")) return "Pre 2.4";
      if(data.f.includes("2DItems/Maps/act4maps")) return "Pre 2.0";
    }

    logger.info(`Invalid map item icon: ${icon}`);
    return "";

  }

  function getSeedValue() {

    var identifier = item.typeline + (getSeedLevel() >= 76 ? " L76+" : "");
    return getValueFromTable("Seed", identifier);

    function getSeedLevel() {
      for(let i = 0; i < item.parsedItem.properties.length; i++) {
        let prop = item.parsedItem.properties[i];
        if(prop.name === "Spawns a Level %0 Monster when Harvested") {
          return prop.values[0][0];
        }
      }
    }

  }


  function getBaseTypeValue() {

    if(item.parsedItem.ilvl < 82) {
      return getVendorRecipeValue();
    }

    var identifier = item.typeline.replace("Superior ", "");
    if(identifier.endsWith("Talisman")) return 0;

    if(item.rarity === "Magic" && item.identified) {
      // strip affixes from magic item name
      identifier = ItemCategoryParser.getEquipmentBaseType(identifier);
    }
    identifier += ` L${item.parsedItem.ilvl > 86 ? 86 : item.parsedItem.ilvl}`;
    if(item.parsedItem.influences) {
      let inf = item.parsedItem.influences;
      if(inf.shaper) {
        identifier += " Shaper";  
      } else if(inf.elder) {
        identifier += " Elder";  
      } else if(inf.crusader) {
        identifier += " Crusader";  
      } else if(inf.redeemer) {
        identifier += " Redeemer";  
      } else if(inf.warlord) {
        identifier += " Warlord";  
      } else if(inf.hunter) {
        identifier += " Hunter";  
      }
    }

    let value = getValueFromTable("BaseType", identifier);
    let vendorValue = getVendorRecipeValue();
    return(vendorValue ? Math.max(value, vendorValue.val) : value);

  }

  function getVendorRecipeValue() {

    var vendorValue;

    var sockets = ItemData.getSockets(item.parsedItem);
    if(sockets.length) {
      if(ItemData.countSockets(sockets) === 6) {
        if(sockets.length === 1) {
          vendorValue = rates["Currency"]["Divine Orb"];
        } else {
          vendorValue = rates["Currency"]["Jeweller's Orb"] * 7;
        }
      } else {
        for(var i = 0; i < sockets.length; i++) {
          if(sockets[i].includes("R") && sockets[i].includes("G") && sockets[i].includes("B")) {
            vendorValue = rates["Currency"]["Chromatic Orb"];
          }
        }
      }
    } else if(item.category && item.category.includes("Skill Gems")) {
      let quality = ItemData.getQuality(item.parsedItem);
      if(quality >= 20) {
        vendorValue = rates["Currency"]["Gemcutter's Prism"];
      }
    }

    if(!vendorValue) {
      return 0;
    } else {

      let currFilter = ItemFilter.getForCategory("currency");
      if(currFilter.ignore) {
        if(currFilter.minValue) {
          if(vendorValue < currFilter.minValue) {
            if(log) {
              logger.info(`Vendor value ${vendorValue} < currency min value ${currFilter.minValue}, returning`);
            }
            return 0;
          }
        } else {
          if(log) {
            logger.info(`Ignoring currency unconditionally?!? Returning 0`);
          }
          return 0;
        }
      }

      if(log) {
        logger.info("Returning vendor value " + vendorValue);
      }
      return { isVendor: true, val: vendorValue };

    }

  }


  function getCurrencyValue() {
    // temporary workaround poe.ninja bug
    // if(item.typeline === "Stacked Deck") return 4 * item.stacksize;

    switch(item.typeline) {
      case "Chaos Orb":
        if(minItemValue > 1) {
          // if we only care about currency greater than 1c in value, chaos orbs are ignored
          return 0;
        } else {
          return item.stacksize;
        }
      case "Chaos Shard":
        if(minItemValue > 1/20) {
          return 0;
        } else {
          return item.stacksize / 20;
        }
      default:
        return getValueFromTable("Currency");
    }

  }

  function getUniqueMapValue() {

    var name = item.name || Utils.getItemName(item.icon);
    var tier = ItemData.getMapTier(item.parsedItem);
    var typeline = item.typeline.replace("Superior ", "");

    var identifier = `${name} T${tier} ${typeline}`;

    return getValueFromTable("UniqueMap", identifier);

  }

  function getUniqueItemValue() {

    var identifier = item.name || Utils.getItemName(item.icon) || item.typeline;

    if(identifier === "Grand Spectrum" || identifier === "Combat Focus") {
      identifier += ` ${item.typeline}`;
    } else if(identifier === "Impresence") {
      var variant = item.icon.replace("https://web.poecdn.com/image/Art/2DItems/Amulets/Elder", "").replace(".png", "");
      identifier += ` (${variant})`;
    }

    var links = getLinks(item.parsedItem);
    identifier += links;
    identifier += getAbyssSockets(identifier);

    if(item.identified === 0) {
      var arr = null;
      if(identifier === "Agnerod") {
        arr = [
          `Agnerod East${links}`,
          `Agnerod North${links}`,
          `Agnerod South${links}`,
          `Agnerod West${links}`
        ];
      } else if(identifier === "Atziri's Splendour") {
        arr = [
          `Atziri's Splendour${links} (Armour)`,
          `Atziri's Splendour${links} (Armour/ES)`,
          `Atziri's Splendour${links} (Armour/ES/Life)`,
          `Atziri's Splendour${links} (Armour/Evasion)`,
          `Atziri's Splendour${links} (Armour/Evasion/ES)`,
          `Atziri's Splendour${links} (ES)`,
          `Atziri's Splendour${links} (Evasion)`,
          `Atziri's Splendour${links} (Evasion/ES)`,
          `Atziri's Splendour${links} (Evasion/ES/Life)`
        ];
      } else if(identifier === "Yriel's Fostering") {
        arr = [
          `Yriel's Fostering${links} (Bleeding)`,
          `Yriel's Fostering${links} (Maim)`,
          `Yriel's Fostering${links} (Poison)`
        ];
      } else if(identifier === "Doryani's Invitation") {
        arr = [
          `Doryani's Invitation (Cold)`,
          `Doryani's Invitation (Fire)`,
          `Doryani's Invitation (Lightning)`,
          `Doryani's Invitation (Physical)`
        ];
      } else if(identifier === "Volkuur's Guidance") {
        arr = [
          `Volkuur's Guidance (Cold)`,
          `Volkuur's Guidance (Fire)`,
          `Volkuur's Guidance (Lightning)`
        ];
      } else if(identifier === "Vessel of Vinktar") {
        arr = [
          "Vessel of Vinktar (Added Attacks)",
          "Vessel of Vinktar (Penetration)",
          "Vessel of Vinktar (Added Spells)",
          "Vessel of Vinktar (Conversion)"
        ];
      }
      if(arr) {
        return getValueUnidWithVariants(arr);
      }
    }

    let value = getValueFromTable("UniqueItem", identifier);
    let vendorValue = getVendorRecipeValue();
    return(vendorValue ? Math.max(value, vendorValue.val) : value);

  }

  function getValueUnidWithVariants(arr) {

    var min = 9999999;
    var max = -1;

    arr.forEach(ident => {
      var val = rates["UniqueItem"][ident];
      if(val < min) min = val;
      if(val > max) max = val;
    });

    var value = (min + max) / 2;
    return(value >= minItemValue ? value : 0);

  }

  function getLinks(item) {
    var sockets = ItemData.getSockets(item);
    for(var i = 0; i < sockets.length; i++) {
      if(sockets[i].length >= 5) {
        return ` ${sockets[i].length}L`;
      }
    }
    return "";
  }

  function getAbyssSockets(identifier) {

    const abyssItems = [
      "Bubonic Trail",
      "Tombfist",
      "Lightpoacher",
      "Shroud of the Lightless"
      // currently bugged on poe.ninja
      //"Hale Negator",
      //"Command of the Pit"
    ];
    if(!abyssItems.includes(identifier)) return "";

    var numAbyssSockets = item.sockets.match(/A/g).length;
    switch(numAbyssSockets) {
      case 1:
        return ` (1 Jewel)`;
        break;
      case 2:
        return ` (2 Jewels)`;
        break;
      default:
        return "";
        break;
    }

  }

}

async function getCurrencyByName(timestamp, type, league) {
  var rates = await getRatesFor(timestamp, league);
  if(!rates) {
    return 0;
  }
  var value = rates["Currency"][type];
  if(!value) {
    //logger.info(`Could not find value for ${item.typeline}`);
    return 0;
  } else {
    //logger.info(`${type} => ${value}`);
    return value;
  }
}

